package CountSubsequentStrings;

public class CountSubsequentStrings {
    public static int solve(String A) {
        int len = A.length();
        int first_character_count = 0;
        int answer = 0;
        for(int i=0; i<len; i++){
            if(A.charAt(i) == 'A')
                first_character_count++;
            else if (A.charAt(i) == 'G')
                answer+=first_character_count;
        }
        return answer;
    }
    public static void main(String args[]) {
        int ans = 0;
        String str = "";
        ans = solve(str);
        System.out.println("Answer ::::  " +ans);
    }

}
